function obtener_usuarios() {
    cliente = $("#RCliente").val();
    existe = true
    if (cliente == "") {
        Swal.fire({
            icon: 'error',
            title: 'Oops...',
            text: 'Â¡Complete los campos vacios.!',
        })
        return;
    }
    var myHeader = new Headers({
        'Authorization': token
    });
    usuarios = api + "usuarios";
    myHeader.append("Content-Type", "application/json",);
    var raw = JSON.stringify({ "NOM_USUARIO": user_logeado, "COD_USUARIO": id_user, "COD_MODULO": 9, });
    var requestOptions = {
        method: 'POST',
        headers: myHeader,
        body: raw,
        redirect: 'follow'
    };
    fetch(usuarios, requestOptions)
        .then(function (response) {
            return response.json();
        })
        .then(function (data) {
            // console.log(data[0])
            data[0].forEach(element => {
                //  console.log(element.Usuario!= cliente)

                if (element.Usuario == cliente) {
                    existe = false;
                    buscar_usuario(cliente)
                    return;
                }

            });
            if (existe) {
                Swal.fire({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'Â¡El Usuario no es valido, favor introducir el codigo correcto.!',
                })
                return;
            }


        })
        .catch(function (err) {
            console.log(err);
        });

}
